package dev.amrw.playground.java.app;

import dev.amrw.playground.java.list.LinkedList;

import static dev.amrw.playground.java.utilities.StringUtils.join;
import static dev.amrw.playground.java.utilities.StringUtils.split;
import static dev.amrw.playground.java.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
